import { People, Person } from '../types.js';

declare const isPersonArray: (obj: unknown) => obj is Person[];
declare const isPerson: (obj: unknown) => obj is Person;
/**
 * Validate 'people' fields, which can be an object like this:
 *
 * {
 *   "name" : "Barney Rubble"
 *   "email" : "b@rubble.com",
 *   "url" : "http://barnyrubble.tumblr.com/"
 * }
 *
 * Or a single string like this:
 * "Barney Rubble &lt;b@rubble.com> (http://barnyrubble.tumblr.com/)
 * Or an array of either of the above.
 */
declare const validatePeople: (name: string, obj: People) => string[];

export { isPerson, isPersonArray, validatePeople };
